import React, { useState } from 'react';
import generator from "generate-password";
import './random.css'
 
function Random() {
  const [password, setPassword] = useState('');
  const [length, setLength] = useState(10);
  const [isLowerCase, setIsLowerCase] = useState(true);
  const [isUpperCase, setIsUpperCase] = useState(false);
  const [isNumbers, setIsNumbers] = useState(false);
  const [isSymbols, setIsSymbols] = useState(false);
 
//   const generatePassword = () => {
//     const pwd = generator.generate({
//       length: length,
//       lowercase: isLowerCase,
//       uppercase: isUpperCase,
//       numbers: isNumbers,
//       symbols: isSymbols
//     });
//     setPassword(pwd);
    
//   }

 
  return (
    <div className="container">
  <h5>Generate a random password</h5>
  <div className="row">
    <div className="col">
      <label>
        <span className="lbl-len">Length:</span>
        <input type="text" className="input-len form-control"  />
      </label>
      {console.log("pass",password)}
    </div>
  </div>
  <div className="row">
    <div className="col">
      <label className="form-control">
        <input type="checkbox" className="mr-1" checked="{isLowerCase}" onChange={() => setIsLowerCase(val => !val)} />
        <span>LowerCase</span>
      </label>
    </div>
    <div className="col">
      <label className="form-control">
        <input type="checkbox" className="mr-1" checked="{isUpperCase}" onChange={() => setIsUpperCase(val => !val)} />
        <span>UpperCase</span>
      </label>
    </div>
  </div>
  <div className="row">
    <div className="col">
      <label className="form-control">
        <input type="checkbox" className="mr-1" checked="{isNumbers}" onChange={() => setIsNumbers(val => !val)} />
        <span>Numbers</span>
      </label>
    </div>
    <div className="col">
      <label className="form-control">
        <input type="checkbox" className="mr-1" checked="{isSymbols}" onChange={() => setIsSymbols(val => !val)} />
        <span>Symbols</span>
      </label>
    </div>
  </div>
  <small className="small-text">Note: At least one option should be selected.</small>
  <div className="row">
    <div className="col">
      {/* <input type="button" className="btn btn-dark mt-2 mb-3" value="Generate Password" onClick={generatePassword} /> */}
      <div className="password">
        Password: {password}
      </div>
    </div>
  </div>
</div>

  );
}
 
export default Random;
